div.script:
    // Menu Navigation Handler
    document.addEventListener('DOMContentLoaded', function() {
        // Handle menu item clicks
        var menuItems = document.querySelectorAll('.dashboard-sidebar .menu-item');
        menuItems.forEach(function(item) {
            item.addEventListener('click', function(e) {
                e.preventDefault();

                // Remove active class from all menu items
                menuItems.forEach(function(mi) {
                    mi.classList.remove('active');
                });

                // Add active class to clicked item
                this.classList.add('active');

                // Hide all page sections
                var pages = document.querySelectorAll('.page-section');
                pages.forEach(function(page) {
                    page.classList.remove('active');
                });

                // Show selected page
                var pageId = this.getAttribute('data-page');
                if (pageId) {
                    var targetPage = document.getElementById('page-' + pageId);
                    if (targetPage) {
                        targetPage.classList.add('active');
                    }
                }
            });
        });

        // Add click handlers for better UX
        var cards = document.querySelectorAll('.stat-card, .sentiment-circle, .conversation-item');
        cards.forEach(function(card) {
            card.addEventListener('mouseenter', function() {
                this.style.cursor = 'pointer';
            });
        });

        // Add tooltip on hover for sentiment circles
        var sentimentCircles = document.querySelectorAll('.sentiment-circle');
        sentimentCircles.forEach(function(circle) {
            circle.addEventListener('mouseenter', function(e) {
                var tooltip = document.createElement('div');
                tooltip.className = 'sentiment-tooltip';
                tooltip.style.cssText = 'position: absolute; background: rgba(0,0,0,0.8); color: white; padding: 8px 12px; border-radius: 6px; font-size: 12px; pointer-events: none; z-index: 1000; margin-top: -40px;';
                tooltip.textContent = 'Click to view ' + (this.textContent.match(/Positive|Neutral|Negative/)[0] || '') + ' interviews';
                this.appendChild(tooltip);
            });
            circle.addEventListener('mouseleave', function() {
                var tooltip = this.querySelector('.sentiment-tooltip');
                if (tooltip) tooltip.remove();
            });
    });

    // Candidates page search functionality
    var candidateSearch = document.getElementById('candidate-search');
    if (candidateSearch) {
        candidateSearch.addEventListener('input', function() {
            var searchTerm = this.value.toLowerCase();
            var cards = document.querySelectorAll('.candidate-card');
            var visibleCount = 0;

            cards.forEach(function(card) {
                var name = card.querySelector('h3').textContent.toLowerCase();
                var position = card.querySelector('p').textContent.toLowerCase();
                var email = '';
                var emailEl = card.querySelector('.fa-envelope');
                if (emailEl && emailEl.parentElement) {
                    email = emailEl.parentElement.textContent.toLowerCase();
                }

                if (name.includes(searchTerm) || position.includes(searchTerm) || email.includes(searchTerm)) {
                    card.style.display = 'block';
                    visibleCount++;
                } else {
                    card.style.display = 'none';
                }
            });

            var countEl = document.getElementById('candidate-count');
            if (countEl) {
                countEl.textContent = visibleCount;
            }
        });
    }

    // View toggle functionality
    var viewToggles = document.querySelectorAll('.view-toggle');
    viewToggles.forEach(function(toggle) {
        toggle.addEventListener('click', function() {
            viewToggles.forEach(function(t) {
                t.classList.remove('active');
                if (t.getAttribute('data-view') === 'grid') {
                    t.style.background = 'transparent';
                    t.style.color = '#666';
                } else {
                    t.style.background = 'transparent';
                    t.style.color = '#666';
                }
            });

            this.classList.add('active');
            this.style.background = '#667eea';
            this.style.color = 'white';

            var view = this.getAttribute('data-view');
            var grid = document.getElementById('candidates-grid');
            if (grid) {
                if (view === 'list') {
                    grid.style.gridTemplateColumns = '1fr';
                } else {
                    grid.style.gridTemplateColumns = 'repeat(auto-fill, minmax(320px, 1fr))';
                }
            }
        });
    });

    // Conversations page search functionality
    var conversationSearch = document.getElementById('conversation-search');
    if (conversationSearch) {
        conversationSearch.addEventListener('input', function() {
            var searchTerm = this.value.toLowerCase();
            var cards = document.querySelectorAll('.conversation-card');
            var visibleCount = 0;

            cards.forEach(function(card) {
                var name = card.querySelector('h4').textContent.toLowerCase();
                var callId = '';
                var callIdEl = card.querySelector('.fa-phone');
                if (callIdEl && callIdEl.parentElement) {
                    callId = callIdEl.parentElement.textContent.toLowerCase();
                }
                var phone = '';
                var phoneEl = card.querySelector('p');
                if (phoneEl) {
                    phone = phoneEl.textContent.toLowerCase();
                }

                if (name.includes(searchTerm) || callId.includes(searchTerm) || phone.includes(searchTerm)) {
                    card.style.display = 'flex';
                    visibleCount++;
                } else {
                    card.style.display = 'none';
                }
            });

            var countEl = document.getElementById('conversation-count');
            if (countEl) {
                countEl.textContent = visibleCount;
            }
        });
    }
});
